    possibleAnswers = ["Transfer√™ncias","Extrato","Erro na m√°quina","Alterar dados","+ op√ß√µes"] 
    if(possibleAnswers.indexOf(input)!=-1){
        var r=input.trim().toLowerCase();
        r = r.split(' ').join('_');
        r = r.replace(new RegExp(/\s/g),"");
        r = r.replace(new RegExp(/[√†√°√¢√£√§√•]/g),"a");
