        r = r.replace(new RegExp(/[√¨√≠√Æ√Ø]/g),"i");
        r = r.replace(new RegExp(/√±/g),"n");                
        r = r.replace(new RegExp(/[√≤√≥√¥√µ√∂]/g),"o");
        r = r.replace(new RegExp(/≈ì/g),"oe");
        r = r.replace(new RegExp(/[√π√∫√ª√º]/g),"u");
        r = r.replace(new RegExp(/[√Ω√ø]/g),"y");
