function run(smallTalksResponse) {
    let  response = JSON.parse(smallTalksResponse)
    if (response && response.analysis) {
        let matches = response.analysis.matches;
        if (matches && matches.length) {
            let smallTalksMatched = [];

            for(let i = 0; i < matches.length; i++) {
                smallTalksMatched.push(matches[i].smallTalk)
            }

            if (smallTalksMatched.indexOf('Sorry') != -1) {
                return 'Desculpa';
            } else if (smallTalksMatched.indexOf('Thank') != -1) {
                return 'Agradecimento';
            } else if (smallTalksMatched.indexOf('GoodBye') != -1) {
                return 'Adeus';
            } else if (smallTalksMatched.indexOf('Cordiality') != -1) {
                return 'Cordialidade';
            } else if (smallTalksMatched.indexOf('GoodMorning') != -1) {
                return 'Bom dia';
            } else if (smallTalksMatched.indexOf('GoodAfternoon') != -1) {
                return 'Boa tarde';
            } else if (smallTalksMatched.indexOf('GoodNight') != -1) {
                return 'Boa noite';
            } else if (smallTalksMatched.indexOf('Greeting') != -1) {
                return 'Saudacao';
            }
        }
    }
    return "Nao identificado";
}

