function run(lastInteractionBeforeCrisis, interval) {
    if (lastInteractionBeforeCrisis && interval) {
        let crisisInterval = JSON.parse(interval)
        if (crisisInterval.status != 'success')
            return true
        let lastDate = new Date(JSON.parse(lastInteractionBeforeCrisis));
        let dateTimeNow = new Date();
        let test = dateTimeNow.getTime() - lastDate.getTime();
        let timeDiff = Math.abs(dateTimeNow.getTime() - lastDate.getTime());
        let diffHours = timeDiff / (1000 * 3600); 
        return diffHours > crisisInterval.resource;
    }
    return true; //Return value will be saved as "Return value variable" field name
}

